import React, { useState, useEffect } from 'react'; // Reactとフックをインポートします
import axios from 'axios'; // HTTPリクエストを行うためのaxiosをインポートします
import MapComponent from './MapComponent'; // マップ表示用のコンポーネントをインポートします

function App() {
  const [message, setMessage] = useState(''); // messageというステートを定義し、初期値を空文字に設定します

  useEffect(() => {
    // コンポーネントのマウント時に実行される副作用を設定します
    axios.get('http://localhost:5000/api') // サーバーからデータを取得します
      .then(response => {
        setMessage(response.data.message); // 取得したデータをステートに設定します
      })
      .catch(error => {
        console.error("Error fetching data: ", error); // エラーが発生した場合の処理
      })
  }, []); // 空の依存配列を渡すことで、コンポーネントのマウント時に一度だけ実行されるようにします

  return (
    <div className="App">
      <h1>マップサイト(仮)</h1> {/* サイトのタイトルを表示します */}
      <p>backend/server.jsから: {message}</p> {/* サーバーから取得したメッセージを表示します */}
      <MapComponent /> {/* マップ表示用のコンポーネントを表示します */}
    </div>
  );
}

export default App; // Appコンポーネントをエクスポートします
